import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import moment from 'moment';

import { logout } from '../../actions/authActions';

import { loadDuesPaid } from '../../actions/paymentActions';
import history from '../../utils/history';
import { ReactComponent as Logo } from '../../assets/udupay.svg';
import Spinner from '../../components/spinner/spinner';

import {
  LogoContainer,
  Support,
  Nav,
  LogoutLink,
  SearchField,
  Divider,
  PaymentList,
  FooterCopyright,
} from './payment-history.styles';

function PaymentHistory({
  userDetails,
  loadDuesPaid,
  logout,
  payment: { paid, loading },
}) {
  useEffect(() => {
    loadDuesPaid();
  }, [loadDuesPaid]);

  return userDetails === null ? (
    <Spinner />
  ) : (
    <>
      <div>
        <Support>
          <p onClick={() => history.push('/dashboard')}>Dashboard</p>
          <p>Support/Help</p>
          <LogoutLink onClick={logout} to="/">
            Logout
          </LogoutLink>
        </Support>
        <Nav>
          <LogoContainer to="/">
            <Logo className="logo" />
          </LogoContainer>
          <p>
            {/* <img src={userDetails.user[0].photo} alt="users" /> */}
            Hi {userDetails.user[0].first_name.toUpperCase()}{' '}
            {userDetails.user[0].last_name.toUpperCase()}
          </p>
        </Nav>
      </div>

      <PaymentList>
        <SearchField>
          <img
            className="search-icon"
            alt="search icon"
            src="data:image/svg+xml;utf8;base64,"
          />
          <input placeholder="Search" type="text" className="search" />
          <img
            className="clear-icon"
            alt="search"
            src="data:image/svg+xml;utf8;base64,"
          />
        </SearchField>

        <p>Transaction List</p>

        <Divider role="separator"></Divider>

        {loading ? (
          <Spinner />
        ) : (
          <table className="tftable">
            <thead>
              <tr>
                <th>Date</th>
                <th>Payment Type</th>
                <th>Amount (₦)</th>
              </tr>
            </thead>

            {paid.due.map((levy, i) => (
              <tbody key={i}>
                <tr>
                  <td>
                    {moment(levy.created_at).format('MMM Do, YYYY | h:mm a')}
                  </td>
                  <td>{levy.name}</td>
                  <td>{levy.amount}</td>
                </tr>
              </tbody>
            ))}
          </table>
        )}
      </PaymentList>

      <FooterCopyright>
        <p>UduPay. © 2020</p>
      </FooterCopyright>
    </>
  );
}

const mapStateToProps = state => ({
  dashTabElem: state.dashboard.tabs,
  userDetails: state.auth.user,
  payment: state.payment,
});

export default connect(mapStateToProps, { loadDuesPaid, logout })(
  PaymentHistory,
);
